import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../services/auth';

const AuthPage = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [error, setError] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const errorParam = params.get('error');
    const token = params.get('token');
    
    if (errorParam) {
      console.error('Erro de autenticação:', errorParam);
      setError('Erro na autenticação. Por favor, tente novamente.');
    } else if (token) {
      localStorage.setItem('authToken', token);
      login({ token });
      navigate('/', { replace: true });
    }
  }, [navigate, login]);

  const handleGoogleLogin = () => {
    console.log('Iniciando login com Google');
    window.location.href = `${process.env.REACT_APP_BACKEND_URL}/auth/google/login`;
  };

  const handleLinkedInLogin = () => {
    console.log('Iniciando login com LinkedIn');
    window.location.href = `${process.env.REACT_APP_BACKEND_URL}/auth/linkedin/login`;
  };

  return (
    <div className="container">
      <div className="header">
        <img src="/img/logo.jpg" alt="RH Super Sincero Logo" />
        <h1>RH Super Sincero</h1>
      </div>

      <div className="section">
        <h2>Bem-vindo ao RH Super Sincero</h2>
        <p>Faça login para começar a análise do seu currículo</p>
        
        {error && (
          <div className="error-message">
            <p>{error}</p>
          </div>
        )}

        <div className="auth-buttons">
          <button 
            className="login-button google-button"
            onClick={handleGoogleLogin}
          >
            Continuar com Google
          </button>

          <button 
            className="login-button linkedin-button"
            onClick={handleLinkedInLogin}
          >
            Continuar com LinkedIn
          </button>
        </div>

        <div className="section">
          <h3>Como funciona:</h3>
          <ul>
            <li>Faça login com sua conta Google ou LinkedIn</li>
            <li>Envie seu currículo em formato PDF ou DOCX</li>
            <li>Adicione links das vagas desejadas</li>
            <li>Receba uma análise detalhada e personalizada</li>
            <li>Otimize seu currículo com nossas recomendações</li>
          </ul>
        </div>

        <p className="terms">
          Ao fazer login, você concorda com nossos Termos de Serviço e Política de Privacidade
        </p>
      </div>
    </div>
  );
};

export default AuthPage;
