import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../services/auth';

const AuthPage = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    
    if (token) {
      localStorage.setItem('authToken', token);
      navigate('/');
    }
  }, [navigate]);

  const handleGoogleLogin = () => {
    window.location.href = `${process.env.REACT_APP_BACKEND_URL}/auth/google/login`;
  };


  const handleLinkedInLogin = () => {
    const backendUrl = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8000';
    console.log('Iniciando login com LinkedIn...');
    window.location.href = `${backendUrl}/auth/linkedin/login`;
  };

  return (
    <div className="auth-container">
      <div className="auth-content">
        <img 
          src="/img/logo.jpg" 
          alt="RH Super Sincero Logo" 
          className="auth-logo"
          style={{ maxWidth: '150px' }}
        />
        <h1>RH Super Sincero</h1>
        <p>Faça login para começar a análise do seu currículo</p>
        
        <div className="auth-buttons">
          <button 
            className="auth-button google-button"
            onClick={handleGoogleLogin}
            style={{ marginBottom: '10px' }}
          >
            Continuar com Google
          </button>

          <button 
            className="auth-button linkedin-button"
            onClick={handleLinkedInLogin}
          >
            Continuar com LinkedIn
          </button>
        </div>

        <p className="auth-terms">
          Ao fazer login, você concorda com nossos Termos de Serviço e Política de Privacidade
        </p>
      </div>
    </div>
  );
};

export default AuthPage;
