import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../services/auth';

const AuthPage = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [isRegistering, setIsRegistering] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: ''
  });
  const [error, setError] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    
    if (token) {
      localStorage.setItem('authToken', token);
      login({ token });
      navigate('/', { replace: true });
    }
  }, [navigate, login]);

  const handleGoogleLogin = () => {
    window.location.href = `${process.env.REACT_APP_BACKEND_URL}/auth/google/login`;
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (isRegistering) {
      // Validações do formulário de registro
      if (formData.password !== formData.confirmPassword) {
        setError('As senhas não coincidem');
        return;
      }

      try {
        const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/auth/register`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            email: formData.email,
            password: formData.password,
            name: formData.name
          }),
        });

        if (!response.ok) {
          throw new Error('Erro ao criar conta');
        }

        const data = await response.json();
        localStorage.setItem('authToken', data.token);
        login({ token: data.token });
        navigate('/');
      } catch (err) {
        setError('Erro ao criar conta. Por favor, tente novamente.');
      }
    } else {
      // Login tradicional
      try {
        const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/auth/login`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            email: formData.email,
            password: formData.password,
          }),
        });

        if (!response.ok) {
          throw new Error('Credenciais inválidas');
        }

        const data = await response.json();
        localStorage.setItem('authToken', data.token);
        login({ token: data.token });
        navigate('/');
      } catch (err) {
        setError('Email ou senha inválidos');
      }
    }
  };

  return (
    <div className="container">
      <div className="header">
        <img src="/img/logo.jpg" alt="RH Super Sincero Logo" />
        <h1>RH Super Sincero</h1>
      </div>

      <div className="section">
        <h2>Bem-vindo ao RH Super Sincero</h2>
        <p>Faça login para começar a análise do seu currículo</p>
        
        <div className="auth-buttons">
          <button 
            className="login-button google-button"
            onClick={handleGoogleLogin}
          >
            Continuar com Google
          </button>
        </div>

        <div className="auth-separator">
          <span>ou</span>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          {error && <div className="error-message">{error}</div>}
          
          {isRegistering && (
            <div className="form-group">
              <label htmlFor="name">Nome completo</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Senha</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
          </div>

          {isRegistering && (
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirmar senha</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                required
              />
            </div>
          )}

          <button type="submit" className="submit-button">
            {isRegistering ? 'Criar conta' : 'Entrar'}
          </button>
        </form>

        <p className="auth-switch">
          {isRegistering ? (
            <>
              Já tem uma conta?{' '}
              <button onClick={() => setIsRegistering(false)}>
                Faça login
              </button>
            </>
          ) : (
            <>
              Não tem uma conta?{' '}
              <button onClick={() => setIsRegistering(true)}>
                Cadastre-se
              </button>
            </>
          )}
        </p>

        <p className="terms">
          Ao fazer login, você concorda com nossos Termos de Serviço e Política de Privacidade
        </p>
      </div>
    </div>
  );
};

export default AuthPage;
