import React from 'react';
import { Button, Container, Paper, Typography, Box } from '@mui/material';
import GoogleIcon from '@mui/icons-material/Google';
import LinkedInIcon from '@mui/icons-material/LinkedIn';

const AuthPage = () => {
  const handleGoogleLogin = () => {
    window.location.href = `${process.env.REACT_APP_BACKEND_URL}/auth/google/login`;
  };

  const handleLinkedInLogin = () => {
    window.location.href = `${process.env.REACT_APP_BACKEND_URL}/auth/linkedin/login`;
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Paper elevation={3} sx={{ p: 4, width: '100%' }}>
          <Box sx={{ mb: 4, textAlign: 'center' }}>
            <img src="/img/logo.jpg" alt="RH Super Sincero Logo" style={{ maxWidth: 200 }} />
            <Typography variant="h4" component="h1" gutterBottom>
              RH Super Sincero
            </Typography>
            <Typography variant="body1" color="text.secondary" paragraph>
              Faça login para começar a análise do seu currículo
            </Typography>
          </Box>

          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <Button
              variant="contained"
              startIcon={<GoogleIcon />}
              onClick={handleGoogleLogin}
              fullWidth
              size="large"
              sx={{ 
                backgroundColor: '#DB4437',
                '&:hover': { backgroundColor: '#C53929' }
              }}
            >
              Continuar com Google
            </Button>

            <Button
              variant="contained"
              startIcon={<LinkedInIcon />}
              onClick={handleLinkedInLogin}
              fullWidth
              size="large"
              sx={{ 
                backgroundColor: '#0077B5',
                '&:hover': { backgroundColor: '#006097' }
              }}
            >
              Continuar com LinkedIn
            </Button>
          </Box>

          <Box sx={{ mt: 4, textAlign: 'center' }}>
            <Typography variant="body2" color="text.secondary">
              Ao fazer login, você concorda com nossos Termos de Serviço e Política de Privacidade
            </Typography>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default AuthPage;
